import Footer from "@/components/Footer";
import { Heart, Users, Leaf, Home, MapPin, Phone, Mail, Calendar, Info, Filter, X, HelpCircle, CheckCircle } from "lucide-react";
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from "@/components/ui/tooltip";
import { useState, useEffect } from "react";

const Comunidades = () => {
  const [filtrosActivos, setFiltrosActivos] = useState<any>({});
  const [mostrarFiltros, setMostrarFiltros] = useState(false);
  const [loading, setLoading] = useState(true);
  
  // Simular carga de datos
  useEffect(() => {
    setTimeout(() => setLoading(false), 1000);
  }, []);

  // Atajos de teclado
  useEffect(() => {
    const handleKeyPress = (e: KeyboardEvent) => {
      if (e.key === 'f' && (e.ctrlKey || e.metaKey)) {
        e.preventDefault();
        setMostrarFiltros(!mostrarFiltros);
      }
      if (e.key === '?') {
        alert('Atajos de teclado:\n\nCtrl/Cmd + F: Abrir filtros\nEsc: Cerrar filtros\n?: Ver ayuda completa');
      }
      if (e.key === 'Escape' && mostrarFiltros) {
        setMostrarFiltros(false);
      }
    };
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [mostrarFiltros]);

  const aplicarFiltro = (categoria: string, valor: string) => {
    setFiltrosActivos((prev: any) => ({
      ...prev,
      [categoria]: prev[categoria]?.includes(valor)
        ? prev[categoria].filter((v: string) => v !== valor)
        : [...(prev[categoria] || []), valor]
    }));
  };

  const limpiarFiltros = () => {
    setFiltrosActivos({});
  };

  const filtrosDisponibles = [
    { 
      label: "Tipo", 
      icon: <Home className="w-4 h-4" />, 
      opciones: ["Agrícola", "Costera", "Artesanal", "Ecológica"]
    },
    { 
      label: "Actividades", 
      icon: <Leaf className="w-4 h-4" />, 
      opciones: ["Agroturismo", "Pesca", "Artesanías", "Conservación"]
    },
    { 
      label: "Tamaño", 
      icon: <Users className="w-4 h-4" />, 
      opciones: ["Pequeña (50-100)", "Mediana (100-200)", "Grande (200+)"]
    }
  ];

  const SkeletonCard = () => (
    <div className="bg-white rounded-xl border border-gray-200 overflow-hidden animate-pulse">
      <div className="w-full h-48 bg-gray-200"></div>
      <div className="p-6 space-y-4">
        <div className="h-6 bg-gray-200 rounded w-3/4"></div>
        <div className="h-4 bg-gray-200 rounded w-1/2"></div>
        <div className="h-16 bg-gray-200 rounded"></div>
        <div className="space-y-2">
          <div className="h-4 bg-gray-200 rounded"></div>
          <div className="h-4 bg-gray-200 rounded w-5/6"></div>
        </div>
        <div className="h-10 bg-gray-200 rounded"></div>
      </div>
    </div>
  );
  const comunidades = [
    {
      nombre: "Comunidad San Isidro",
      ubicacion: "Parroquia Chirijos",
      habitantes: "150 familias",
      actividades: ["Agroturismo", "Artesanías", "Gastronomía tradicional"],
      imagen: "data:image/jpeg;base64,",
      descripcion: "Comunidad agrícola dedicada al cultivo de cacao y café orgánico. Ofrecen experiencias de cosecha y procesamiento.",
      destacado: "Tour del Cacao"
    },
    {
      nombre: "Pueblo Manta Ancestral",
      ubicacion: "Zona Costera",
      habitantes: "200 familias",
      actividades: ["Pesca artesanal", "Cultura marítima", "Cocina costeña"],
      imagen: "data:image/jpeg;base64,",
      descripcion: "Pescadores tradicionales que mantienen vivas las técnicas ancestrales de pesca y preparación de mariscos.",
      destacado: "Jornada de Pesca"
    },
  
    {
      nombre: "Artesanos de Montecristi",
      ubicacion: "Montecristi Centro",
      habitantes: "80 familias",
      actividades: ["Tejido de sombreros", "Artesanías", "Talleres culturales"],
      imagen: "data:image/jpeg;base64,",
      descripcion: "Maestros tejedores del famoso sombrero de paja toquilla, patrimonio cultural inmaterial de la humanidad.",
      destacado: "Taller de Tejido"
    },
    {
      nombre: "chinborazo",
      ubicacion: "Sector Rural Norte",
      habitantes: "100 familias",
      actividades: ["Agricultura orgánica", "Permacultura", "Medicina ancestral"],
      imagen: "data:image/jpeg;base64,",
      descripcion: "Comunidad enfocada en prácticas agrícolas sostenibles y rescate de conocimientos ancestrales.",
      destacado: "Huerto Comunitario"
    },
    {
      nombre: "bahia de caraquez",
      ubicacion: "Costa Sur",
      habitantes: "180 familias",
      actividades: ["Turismo de playa", "Deportes acuáticos", "Conservación marina"],
      imagen: "data:image/jpeg;base64,",
      descripcion: "Protectores de playas vírgenes que promueven el turismo responsable y la limpieza de costas.",
      destacado: "Limpieza de Playas"
    }
  ];

  const beneficios = [
    { icon: <Heart className="w-8 h-8 text-emerald-600" />, titulo: "Impacto Social Directo", descripcion: "100% de los ingresos van directamente a las comunidades" },
    { icon: <Users className="w-8 h-8 text-emerald-600" />, titulo: "Intercambio Cultural", descripcion: "Experiencias auténticas de convivencia e intercambio" },
    { icon: <Leaf className="w-8 h-8 text-emerald-600" />, titulo: "Turismo Sostenible", descripcion: "Prácticas que protegen el medio ambiente y la cultura" },
    { icon: <Home className="w-8 h-8 text-emerald-600" />, titulo: "Hospedaje Comunitario", descripcion: "Vive con las familias y conoce su día a día" }
  ];

  const comoParticipar = [
    { paso: "1", titulo: "Elige una Comunidad", descripcion: "Selecciona la comunidad que más te interese según tus preferencias" },
    { paso: "2", titulo: "Contacta Directamente", descripcion: "Comunícate con los coordinadores comunitarios para planificar tu visita" },
    { paso: "3", titulo: "Coordina tu Visita", descripcion: "Define fechas, actividades y servicios que deseas experimentar" },
    { paso: "4", titulo: "Vive la Experiencia", descripcion: "Disfruta de una experiencia auténtica y contribuye al desarrollo local" }
  ];

  return (
    <TooltipProvider>
      <div className="min-h-screen bg-gray-50 w-full">
        <div className="pt-12 pb-16 container mx-auto px-4 sm:px-6 lg:px-8">
          {/* Header con Ayuda Contextual */}
          <div className="mb-12">
            <div className="flex items-center justify-between mb-4">
              <h1 className="text-4xl font-bold text-gray-900">Comunidades Aliadas</h1>
              <Tooltip>
                <TooltipTrigger asChild>
                  <button className="p-2 hover:bg-gray-100 rounded-full transition-colors">
                    <HelpCircle className="w-6 h-6 text-emerald-600" />
                  </button>
                </TooltipTrigger>
                <TooltipContent side="left" className="max-w-xs">
                  <p className="font-semibold mb-2">Ayuda - Comunidades</p>
                  <p className="text-sm mb-2">Descubre comunidades auténticas comprometidas con el turismo sostenible</p>
                  <div className="text-xs space-y-1 border-t pt-2 mt-2">
                    <p><kbd className="px-1 py-0.5 bg-gray-200 rounded">Ctrl/Cmd + F</kbd>: Abrir filtros</p>
                    <p><kbd className="px-1 py-0.5 bg-gray-200 rounded">Esc</kbd>: Cerrar filtros</p>
                    <p><kbd className="px-1 py-0.5 bg-gray-200 rounded">?</kbd>: Ver ayuda</p>
                  </div>
                </TooltipContent>
              </Tooltip>
            </div>
            <p className="text-lg text-gray-600 max-w-3xl">
              Descubre comunidades rurales auténticas que abren sus puertas para compartir su cultura, 
              tradiciones y forma de vida. El turismo comunitario genera desarrollo local sostenible.
            </p>
            
            {/* Filtros Avanzados */}
            <div className="mt-6 flex gap-4 items-center">
              <Tooltip>
                <TooltipTrigger asChild>
                  <button 
                    onClick={() => setMostrarFiltros(!mostrarFiltros)}
                    className="inline-flex items-center gap-2 px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 transition-colors"
                  >
                    <Filter className="w-4 h-4" />
                    Filtros Avanzados
                  </button>
                </TooltipTrigger>
                <TooltipContent>
                  <p className="text-xs">Filtra comunidades por tipo, actividades y tamaño</p>
                </TooltipContent>
              </Tooltip>
              
              {Object.keys(filtrosActivos).length > 0 && (
                <button
                  onClick={limpiarFiltros}
                  className="inline-flex items-center gap-2 px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors"
                >
                  <X className="w-4 h-4" />
                  Limpiar filtros ({Object.values(filtrosActivos).flat().length})
                </button>
              )}
            </div>

            {/* Panel de Filtros */}
            {mostrarFiltros && (
              <div className="mt-4 p-6 bg-white rounded-xl border border-gray-200 shadow-lg animate-in slide-in-from-top-2">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="font-semibold text-lg">Filtrar Comunidades</h3>
                  <button onClick={() => setMostrarFiltros(false)}>
                    <X className="w-5 h-5 text-gray-500 hover:text-gray-700" />
                  </button>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                  {filtrosDisponibles.map((filtro, idx) => (
                    <div key={idx} className="space-y-2">
                      <div className="flex items-center gap-2">
                        {filtro.icon}
                        <label className="font-medium text-sm">{filtro.label}</label>
                        <Tooltip>
                          <TooltipTrigger>
                            <Info className="w-3 h-3 text-gray-400" />
                          </TooltipTrigger>
                          <TooltipContent>
                            <p className="text-xs">Filtra por {filtro.label.toLowerCase()}</p>
                          </TooltipContent>
                        </Tooltip>
                      </div>
                      <div className="flex flex-wrap gap-2">
                        {filtro.opciones.map((opcion, i) => (
                          <button
                            key={i}
                            onClick={() => aplicarFiltro(filtro.label, opcion)}
                            className={`px-3 py-1.5 rounded-full text-sm transition-all ${
                              filtrosActivos[filtro.label]?.includes(opcion)
                                ? 'bg-emerald-600 text-white shadow-sm'
                                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                            }`}
                          >
                            {opcion}
                          </button>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

        {/* Beneficios */}
        <div className="mb-16">
          <h2 className="text-3xl font-bold mb-8 text-center">¿Por qué elegir Turismo Comunitario?</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {beneficios.map((beneficio, index) => (
              <div key={index} className="bg-white p-6 rounded-xl border border-gray-200 shadow hover:shadow-lg transition-shadow text-center">
                <div className="mb-4 flex justify-center">{beneficio.icon}</div>
                <h3 className="text-lg font-semibold mb-2">{beneficio.titulo}</h3>
                <p className="text-gray-600">{beneficio.descripcion}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Comunidades con Skeleton Loaders */}
        <div className="mb-16">
          <h2 className="text-3xl font-bold mb-8 text-center">Nuestras Comunidades</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {loading ? (
              Array(6).fill(0).map((_, i) => <SkeletonCard key={i} />)
            ) : (
              comunidades.map((comunidad, index) => (
              <div key={index} className="bg-white rounded-xl border border-gray-200 overflow-hidden shadow hover:shadow-lg transition-shadow">
                <img src={comunidad.imagen} alt={comunidad.nombre} className="w-full h-48 object-cover" />
                <div className="p-6">
                  <h3 className="text-xl font-semibold mb-2">{comunidad.nombre}</h3>
                  <div className="flex items-center gap-2 text-sm text-gray-600 mb-2">
                    <MapPin className="w-4 h-4" />
                    <span>{comunidad.ubicacion}</span>
                  </div>
                  <div className="flex items-center gap-2 text-sm text-gray-600 mb-2">
                    <Users className="w-4 h-4" />
                    <span>{comunidad.habitantes}</span>
                  </div>
                  <p className="text-gray-600 text-sm mb-4">{comunidad.descripcion}</p>
                  <span className="inline-block bg-emerald-100 text-emerald-600 text-xs font-medium px-3 py-1 rounded-full mb-2">
                    ⭐ {comunidad.destacado}
                  </span>
                  <div className="flex flex-wrap gap-2 mb-4">
                    {comunidad.actividades.map((actividad, idx) => (
                      <span key={idx} className="text-xs bg-gray-100 text-gray-800 px-2 py-1 rounded">
                        {actividad}
                      </span>
                    ))}
                  </div>
                  <Tooltip>
                    <TooltipTrigger asChild>
                      <button className="w-full bg-emerald-600 text-white py-2 rounded-lg hover:bg-emerald-700 transition-colors font-medium">
                        Conocer más
                      </button>
                    </TooltipTrigger>
                    <TooltipContent>
                      <p className="text-xs">Ver detalles completos y opciones de contacto</p>
                    </TooltipContent>
                  </Tooltip>
                </div>
              </div>
            )))}
          </div>
        </div>

        {/* Cómo Participar con Tooltips */}
        <div className="mb-16 text-center">
          <div className="flex items-center justify-center gap-2 mb-8">
            <h2 className="text-3xl font-bold">¿Cómo Participar?</h2>
            <Tooltip>
              <TooltipTrigger>
                <Info className="w-5 h-5 text-emerald-600" />
              </TooltipTrigger>
              <TooltipContent>
                <p className="text-xs">Proceso simple en 4 pasos para vivir una experiencia auténtica</p>
              </TooltipContent>
            </Tooltip>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {comoParticipar.map((item, index) => (
              <div key={index} className="bg-white p-6 rounded-xl border border-gray-200 shadow hover:shadow-lg transition-shadow">
                <div className="w-12 h-12 bg-emerald-600 text-white rounded-full flex items-center justify-center text-xl font-bold mb-4">
                  {item.paso}
                </div>
                <h3 className="font-semibold mb-2">{item.titulo}</h3>
                <p className="text-sm text-gray-600">{item.descripcion}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </div>
    </TooltipProvider>
  );
};

export default Comunidades;
